SELECT * FROM SD
WHERE SD_CPCODE= #{cpcode}, SD_PRCODE= #{prcode}, SD_PSCODE= #{pscode},
SD_SCCODE= #{SCCODE}, SD_USERID= #{userid}, SD_CODE= #{sdcode}

